mapper.select:
select r.rec_bno, r.corp_id, r.rec_title, r.rec_maintask, r.rec_preference, r.rec_benefit, r.topctg_id, r.btmctg_id, r.toplct_id, r.rec_inputlct, r.rec_qualification, r.rec_deadline, r.rec_viewcnt, r.rec_regdate, r.rec_updatedate, r.thumbFileName, r.attachFileName, c.company_name as companyName from rec_board r left join corpmember c on r.corp_id = c.corp_id
rec_title like concat('%', #{search}, '%')
order by r.rec_bno desc limit #{pageStart}, #{pageSize}

select corp_id as corpId, corp_userid as corpUserId, corp_userpw as corpUserPw, manager_name as managerName, manager_email as managerEmail, manager_tel as managerTel, company_name as companyName,
business_rno as businessRno, business_licenseurl as businessLicenseUrl,
ceo_name as ceoName, company_address as companyAddress, industry_type as industryType, business_type as businessType, corp_joined as corpJoined, corp_updated as corpUpdated, status as status, corp_checkdeleted as corpCheckDeleted from corpmember order by corp_id desc limit #{pageStart}, #{pageSize}

mapper.update:
update corpmember
set status = 'A'
where corp_id = #{value}

update corpmember
set status = 'D'
where corp_id = #{value}

update corpmember
set corp_checkdeleted = 1
where corp_id = #{corpId}

		update member
    	set is_deleted = 1
    	where id = #{id}
