mapper.select:
SELECT COUNT(*)
FROM application
WHERE member_id = #{member_id}
  AND resume_id = #{resume_id}
  AND rec_bno = #{rec_bno}
  AND is_withdrawn = 'N'

mapper.update:
UPDATE application
SET is_withdrawn = 'Y', status = 'REJECTED'
WHERE application_id = #{application_id}

