mapper.insert:
		INSERT INTO member (
          userid, userpw, email, name, phone, addr, pfImage,
          agree_location, agree_email, agree_sms
      ) VALUES (
          #{userid}, #{userpw}, #{email}, #{name}, #{phone}, #{addr}, #{pfImage},
          #{agreeLocation}, #{agreeEmail}, #{agreeSms}
      )

mapper.select:
		SELECT * FROM member
      WHERE userid = #{userid}
        AND userpw = #{userpw}
        AND is_deleted = 0
