mapper.insert:
		INSERT INTO member (
          userid, userpw, email, name, phone, addr, pfImage,
          agree_location, agree_email, agree_sms
      ) VALUES (
          #{userid}, #{userpw}, #{email}, #{name}, #{phone}, #{addr}, #{pfImage},
          #{agreeLocation}, #{agreeEmail}, #{agreeSms}
      )

mapper.select:
		SELECT * FROM member
      WHERE userid = #{userid}
        AND userpw = #{userpw}
        AND is_deleted = 0

mapper.update:
UPDATE member SET addr=#{addr}, phone=#{phone}, pfImage=#{pfImage},
agree_location=#{agreeLocation}, agree_email=#{agreeEmail}, agree_sms=#{agreeSms}
WHERE userid=#{userid} and userpw=#{userpw}

update member
set is_deleted = 1
where userid = #{userid}
