mapper.select:
select rb.rec_bno, cm.corp_userid as corpUserId, cm.company_name as companyName, rb.thumbFileName, rb.rec_regdate, rb.rec_viewcnt from rec_board rb join corpmember cm on cm.corp_id = rb.corp_id
where (rb.rec_title like concat('%',#{search},'%') or cm.company_name like concat('%',#{search},'%') or cm.corp_userid like concat('%',#{search},'%') or rb.rec_regdate like concat('%',#{search},'%'))
order by rb.rec_bno desc limit #{pageStart},#{pageSize}

mapper.insert:
insert into rec_board (
	corp_id, rec_title, rec_maintask, rec_benefit, 
	topctg_id, btmctg_id, toplct_id, rec_qualification, rec_deadline
) values (
	#{corp_id}, #{rec_title}, #{rec_maintask}, #{rec_benefit}, 
	#{topctg_id}, #{btmctg_id}, #{toplct_id}, #{rec_qualification}, #{rec_deadline}
)

insert into rec_file (
	rec_bno, file_type, originalFileName, storedFileName
) values (
	#{rec_bno}, #{file_type}, #{originalFileName}, #{storedFileName}
)
