mapper.select:
select rb.rec_bno, cm.corp_userid as corpUserId, cm.company_name as companyName, rb.rec_title, rb.thumbFileName, rb.rec_regdate, rb.rec_viewcnt from rec_board rb join corpmember cm on cm.corp_id = rb.corp_id
where (rb.rec_title like concat('%',#{search},'%') or cm.company_name like concat('%',#{search},'%') or cm.corp_userid like concat('%',#{search},'%') or rb.rec_regdate like concat('%',#{search},'%'))
order by rb.rec_bno desc limit #{pageStart},#{pageSize}

SELECT resume_id AS resumeId, resume_title AS resumeTitle
FROM resume
WHERE member_id = #{member_id}
